const c=a=>{if(a){let e;switch(a){case"Monthly":e="Month";break;case"Annually":e="Annum";break;case"Quarterly":e="Quarter";break;case"Weekly":e="Week";break;case"Bi-weekly":e="Bi-weekly";break;case"Daily":e="Day";break;default:e="Season";break}return e}},l=a=>{const e=new Date(a),t=["January","February","March","April","May","June","July","August","September","October","November","December"][e.getMonth()],r=String(e.getDate()).padStart(2,"0"),s=e.getFullYear(),o=`${t.slice(0,3)}, ${r}/${s}`;return{monthName:t.slice(0,3),formattedDate:o}},u=a=>{if(a){const[e]=a.split(" "),[n,t,r]=e.split("-");return`${r}/${t}/${n}`}else return a},m=a=>{let e="";switch(a){case"Percentage":e="%";break;case"Money":e="ETB";break;case"Time":e="Hour";break;default:e=""}return e};export{m as M,c as P,u as a,l as f};
